    public function rastreamento($param) 
    { 

        $token = $this->generateToken(( isset($param['renew_token']) ? $param['renew_token'] : false));
        
        if(!isset($param['envios']) || !count($param['envios'])) {
            $this->error = "Nenhum objeto informado";
            return false;
        }
        
        $str_objs = '';
        
        foreach ($param['envios'] as $i) {
            $str_objs.= 'codigosObjetos='.$i->etiqueta_correios.'BR&';
        }
        
        $data = $this->request([
            'token' => $token,
            'url' => $this->base_url . 'srorastro/v1/objetos?' . $str_objs . '&resultado=T',
        ]);
        
        return $data;
        
    }

    public function generateToken($renew = false) 
    {
        $dateUtils = new DateUtils();
        $filename = $this->dir_base . 'token_' . $dateUtils->getNow(false) . '.txt';

        if (is_file($filename)) {
            if ($renew) {
                unlink($filename);
            } else {
                return file_get_contents($filename);
            }
        }

        $data = $this->request([
            'basic_auth' => true,
            'url' => $this->base_url . 'token/v1/autentica/cartaopostagem',
            'json' => ["numero" => $this->cartao_postagem],
        ]);

        if (!$data) {
            echo "FALHA: " . $this->error . "\n";
            return;
        }

        if (!isset($data['token'])) {
            echo "FALHA: " . $this->error . "\n";
            return;
        }

        DB::table('api_correios')->insert([
            'token' => $data['token'],
            'date_insert' => now(),
            'date_expiration' => preg_replace('/T/', ' ', $data['expiraEm']),
        ]);

        file_put_contents($filename, $data['token']);

        return $data['token'];
    }

    public function request($data = array()) 
    {
        $opt_array = array();

        $ch = curl_init();

        if (isset($data['basic_auth'])) {
            $opt_array[] = 'Authorization: Basic ' . base64_encode($this->username . ':' . $this->pass);
            unset($data['token']);
        } else {
            $opt_array[] = 'Authorization: Bearer ' . $data['token'];
        }

        if (isset($data['json']) && $data['json']) {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($data['json']));
            array_push($opt_array, 'Content-Type: application/json');
        }
        if (isset($data['json_raw']) && $data['json_raw']) {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $data['json_raw']);
            array_push($opt_array, 'Content-Type: application/json');
        }

        if ($opt_array) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $opt_array);
        }

        curl_setopt_array($ch, array(
            CURLOPT_URL => $data['url'],
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_FOLLOWLOCATION => true,
        ));

        if (isset($data['show_header']) && $data['show_header']) {
            curl_setopt($ch, CURLOPT_HEADER, true);
            curl_setopt($ch, CURLINFO_HEADER_OUT, true);
        }

        if (isset($data['post']) && $data['post']) {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data['post']));
        }

        $response = curl_exec($ch);
        $info = curl_getinfo($ch);
        curl_close($ch);
        
        if (isset($data['show_response']) && $data['show_response']) {
            echo $response."\n\n";
        }

        if (isset($data['show_header']) && $data['show_header']) {
            print_r($info);
        }


        if(isset($data['return_objeto']) && $data['return_objeto']){
            $json = json_decode($response);
        } else {
            $json = json_decode($response, true);
        }

        if ($info['http_code'] != '200' && $info['http_code'] != '201') {
            if ($json && isset($json['msgs'])) {

                $msg = implode($json['msgs']);
                $this->error = $msg;
            } else if ($json && isset($json['txErro'])) {
                
                $this->error = $json['msgs'];
                
            } else {
                $this->error = "Falha Desconhecida, teste novamente mais tarde: " . $response . " Http: " . print_r($info, true);
            }

            $logModel = new Log();
            $logModel->log(array(
                'type' => "CORREIOS_REST_API",
                'text' => "Falha request: Sent:\n" . print_r($data, true) . "\nReceived; " . $response . "\nInfo:\n" . print_r($info, true)
            ));

            return false;
        }

        if ($json) {
            $json['http_code'] = $info['http_code'];
            return $json;
        }
    }

